        } else if (selectedModel === 'lama') {
            apiUrl = 'https://test-lam.onrender.com/predict/' + formattedReview;
        }

        fetch(apiUrl)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
